MATCH (a:Actor)
WHERE COUNT {
  (a)-[:ACTED_IN]->(m:Movie)
  WHERE m.year >= $sinceYear
} >= $minMovies
RETURN a.name AS actor,
       COUNT {
         (a)-[:ACTED_IN]->(m:Movie)
         WHERE m.year >= $sinceYear
       } AS recentFilms
ORDER BY recentFilms DESC

MATCH (c:Customer)-[:PURCHASED]->(a:Product {name: $productA})
WHERE NOT EXISTS {
  MATCH (c)-[:PURCHASED]->(b:Product {name: $productB})
}
RETURN c.name AS customer, c.email AS email
ORDER BY c.name

CALL apoc.periodic.iterate(
  'MATCH (p:Product) WHERE p.lastUpdated < datetime() - duration("P90D") RETURN p',
  'SET p.status = "archived", p.archivedAt = datetime()',
  {batchSize: 1000, parallel: false}
) YIELD batches, total, errorMessages
RETURN batches, total, errorMessages

