CALL gds.graph.project(
  'social-graph',
  ['Person', 'Company'],
  {KNOWS: {orientation: 'UNDIRECTED'}, WORKS_AT: {orientation: 'NATURAL', properties: ['since']}}
) YIELD graphName, nodeCount, relationshipCount, projectMillis

CALL gds.louvain.stream('social-graph', {
  includeIntermediateCommunities: false
}) YIELD nodeId, communityId
RETURN communityId, collect(gds.util.asNode(nodeId).name) AS members, count(*) AS size
ORDER BY size DESC

CALL gds.betweenness.stream('social-graph', {
  samplingSize: 1000
}) YIELD nodeId, score
RETURN gds.util.asNode(nodeId).name AS name, score
ORDER BY score DESC
LIMIT 10
